      AntdFloatingPanel(
          position: AntdFloatingPanelPosition.top,
          anchors: const [0.3, 0.6, 0.8],
          indicator: AntdBox(
              style: AntdBoxStyle(color: token.colorPrimary),
              child: const Text("我是指示灯")),
          content: SizedBox(
              height: 400,
              child: AntdList(
                  items: data.map((value) {
                return Text(value);
              }).toList())),
          child: AntdBox(
              onTap: () {
                AntdToast.show("背景点击事件");
