  @override
  Widget build(BuildContext context) {
    return const DemoWrapper(child: [
      AntdEmpty(image: AntdImage(image: AssetImage("assets/images/empty.png")))
    ]);
  }
}

class AntdEmptyDescriptionDemo extends StatelessWidget {
